import { apolloClient } from 'smooth/graphql'

export default function Index(props) {
  console.log(props)
  return 'Hello'
}

const Page = gql`
  query Page {
    page(slug: "test") {
      title
    }
  }
`

Index.getInitialProps = async () => {
  const result = await apolloClient.query(Page)
  console.log(result)
  return { hello: 'ok ' }
}
