import 'isomorphic-fetch'
import { apolloClient } from 'smooth/graphql'
import gql from 'graphql-tag'

export default function Index(props) {
  console.log(props)
  return 'Hello'
}

const Page = gql`
  query Page {
    page(slug: "test") {
      title
    }
  }
`

Index.getInitialProps = async () => {
  const result = await apolloClient.query({ query: Page })
  console.log(result)
  return { hello: 'ok ' }
}
